class Room{constructor(n,t,i){this.id=n;this.creatorId=t;this.currentUserId=i}}class Error{constructor(n,t){this.caption=n;this.message=t}}window.ZokuChat.chat={};window.ZokuChat.chat.room=null;var app=new Vue({el:"#chat-app",data:{window:window,connection:(new signalR.HubConnectionBuilder).withUrl("/chatHub").build(),contacts:[],messages:[],errors:[]},methods:{init:()=>{app.connection.on("ReceiveMessages",function(n){n.forEach(function(n){app.messages.push(n)})});app.connection.on("ReceiveContacts",function(n){n.forEach(function(n){app.contacts.push(n)})});app.connection.on("ReceiveMessage",function(n){app.messages.push(n)});app.connection.on("ReceiveDeleteMessage",function(n){let t=app.messages.findIndex(function(t){return t.id===n.id});t>-1&&app.messages.splice(t,1,n)});app.connection.on("ReceiveError",function(n,t){app.errors.push(n,t)});app.connection.start().catch(function(n){return console.error(n.toString())}).then(function(){app.joinRoom().then(function(){app.retrieveMessages()})})},joinRoom:()=>app.connection.invoke("JoinRoom",window.ZokuChat.chat.room.id).catch(function(n){return console.error(n.toString())}),retrieveMessages:()=>app.connection.invoke("GetMessages",window.ZokuChat.chat.room.id).catch(function(n){return console.error(n.toString())}),sendMessage:n=>app.connection.invoke("SendMessage",window.ZokuChat.chat.room.id,n).catch(function(n){return console.error(n.toString())}),sendButtonClick:()=>{$("#message-input").val().length>0&&(app.sendMessage($("#message-input").val()),$("#message-input").val(""))},deleteMessage:n=>app.connection.invoke("DeleteMessage",window.ZokuChat.chat.room.id,n.id).catch(function(n){return console.error(n.toString())}),canDeleteMessage:n=>{let t=window.ZokuChat.chat.room.currentUserId;return t===n.userId||t===window.ZokuChat.chat.room.creatorId}}});