window.ZokuChat=window.ZokuChat||{};window.ZokuChat.AcceptRequestButtonClick=function(n,t){n.removeClass("btn-primary").addClass("btn-success").html("Request Accepted").attr("disabled","disabled");window.ZokuChat.SendAjaxRequest(`/ContactRequests/Accept?requestId=${t}`)};window.ZokuChat.CancelRequestButtonClick=function(n,t){n.html("Request Declined").attr("disabled","disabled");window.ZokuChat.SendAjaxRequest(`/ContactRequests/Cancel?requestId=${t}`)};window.ZokuChat.SendRequestButtonClick=function(n,t){n.removeClass("btn-primary").addClass("btn-success").html("Request Sent").attr("disabled","disabled");window.ZokuChat.SendAjaxRequest(`/ContactRequests/Create?requestedUID=${t}`)};window.ZokuChat.BlockUserButtonClick=function(n,t){n.html("User Blocked").attr("disabled","disabled");window.ZokuChat.SendAjaxRequest(`/BlockedUsers/Block?blockedUID=${t}`)};window.ZokuChat.UnblockUserButtonClick=function(n,t){n.removeClass("btn-primary").addClass("btn-success").html("User Unblocked").attr("disabled","disabled");window.ZokuChat.SendAjaxRequest(`/BlockedUsers/Unblock?blockedUID=${t}`)};window.ZokuChat.RemoveContactButtonClick=function(n,t){n.removeClass("btn-primary").addClass("btn-success").html("Contact Removed").attr("disabled","disabled");window.ZokuChat.SendAjaxRequest(`/Contacts/Remove?contactId=${t}`)};window.ZokuChat.SendAjaxRequest=function(n,t,i){$.ajax({url:n,dataType:"json"}).done(function(n){n.IsSuccessful?t&&t():i&&i()})};window.ZokuChat.GetLocalDateStr=function(n){return`${1+n.getMonth()}/${n.getDate()}/${n.getFullYear()}`};window.ZokuChat.GetLocalTimeStr=function(n){return`${n.toLocaleString("en-US",{hour:"numeric",minute:"numeric",hour12:!0})}`};window.ZokuChat.GetLocalDateTimeStr=function(n){return`${window.ZokuChat.GetLocalDateStr(n)} at ${window.ZokuChat.GetLocalTimeStr(n)}`};